SET NAMES UTF8;
DROP DATABASE IF EXISTS bilibili ;
CREATE DATABASE bilibili CHARSET UTF8;
USE bilibili;
#创建用户信息表
CREATE TABLE b_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
	uname varchar(16) UNIQUE,
    upwd VARCHAR(32),
    email VARCHAR(64) UNIQUE,
    phone VARCHAR(16) UNIQUE,
    avatar VARCHAR(128) DEFAULT 'img/avatar/1.jpg',
    user_name VARCHAR(32),
    gender INT,
    intr VARCHAR(256)
);
#创建首页轮播图表
CREATE TABLE b_index_carousel(
    cid INT PRIMARY KEY auto_increment,
    img VARCHAR(128),
    title VARCHAR(64),
    href VARCHAR(128),
    type VARCHAR(64)
);
#创建首页推荐表
CREATE TABLE b_recommend(
    rid INT PRIMARY KEY auto_increment,
    img VARCHAR(128),
    title VARCHAR(64),
    author VARCHAR(16),
    play VARCHAR(16),
    date CHAR(2),
    href VARCHAR(128)
);
#创建首页视频信息表
CREATE TABLE b_video(
    vid INT PRIMARY KEY AUTO_INCREMENT,
    img VARCHAR(128),
    type VARCHAR(64),
    isLatestNews BOOLEAN,
    isLatestSub BOOLEAN,
    detail VARCHAR(128),
    dur VARCHAR(32),
    playNumber VARCHAR(16),
    danmuNumber VARCHAR(16),
    href VARCHAR(128)
);
#创建番剧信息表
CREATE TABLE b_bangumi_video(
    vid INT PRIMARY KEY AUTO_INCREMENT,
    img VARCHAR(128),
    on_time VARCHAR(8),
    title VARCHAR(32),
    new VARCHAR(16),
    href VARCHAR(128)
);
#创建排行表
CREATE TABLE b_rank(
    rid INT PRIMARY KEY AUTO_INCREMENT,
    type VARCHAR(16),
    img VARCHAR(128),
    isOrigin BOOLEAN,
    title VARCHAR(32),
    point INT,
    name VARCHAR(16),
    public_time VARCHAR(16),
    text VARCHAR(128),
    play INT,
    danmu INT,
    star INT,
    coin INT,
    href VARCHAR(128)
);
#创建特别推荐表
CREATE TABLE b_special_recommend(
    sid INT PRIMARY KEY AUTO_INCREMENT,
    img VARCHAR(128),
    presenter_avatar VARCHAR(128),
    presenter_name VARCHAR(16),
    title VARCHAR(32),
    video_href VARCHAR(128),
    presenter_href VARCHAR(128)
);


